		LambdaQueryWrapper<$!{tableInfo.name}Entity> queryWrapper = Wrappers.<$!{tableInfo.name}Entity>lambdaQuery();
		Page<$!{tableInfo.name}Entity> page = new Page<>(request.getPageNum(),request.getPageSize());
		IPage<$!{tableInfo.name}Entity> pageResult = $!tool.firstLowerCase($!tableInfo.name)Mapper.selectPage(page,queryWrapper);
		return PageResponse.of(pageResult,$!{tableInfo.name}Response.class);

	}


	@Override
	public $!{tableInfo.name}Response getById(Long id) {
		$!{tableInfo.name}Entity result = $!tool.firstLowerCase($!tableInfo.name)Mapper.selectById(id);
		$!{tableInfo.name}Response response = BeanUtils.copy(result, $!{tableInfo.name}Response.class);
		return response;
	}


	@Override
