		$!{tableInfo.name}Entity bean = BeanUtils.copy(request,$!{tableInfo.name}Entity.class);
		return $!tool.firstLowerCase($!tableInfo.name)Mapper.insert(bean);
	}


	@Override
	public Integer update($!{tableInfo.name}Request request) {
		if(Objects.isNull(request.getId())){
			throw new SoException("id不能为空");
		}
		$!{tableInfo.name}Entity bean = BeanUtils.copy(request,$!{tableInfo.name}Entity.class);
		return $!tool.firstLowerCase($!tableInfo.name)Mapper.updateById(bean);
	}


