mapper.select:
SELECT

FROM user_log WHERE 1 = 1

    AND
    (
    username LIKE CONCAT('%', #{logRequest.nameOrDesc}, '%')
    OR
    operation LIKE CONCAT('%', #{logRequest.nameOrDesc}, '%')
    )


    AND create_time >= #{logRequest.startTime}


    AND create_time <= #{logRequest.endTime}


    AND username = #{logRequest.username}

ORDER BY create_time DESC

SELECT count(*) as visitCount, DATE_FORMAT( ul.create_time, '%Y-%m-%d' ) AS time FROM user_log ul
AND ul.create_time >= #{queryTime}
AND ( ul.username LIKE CONCAT('%', #{nameOrDesc}, '%') OR ul.operation LIKE CONCAT('%', #{nameOrDesc}, '%') )
AND ul.username = #{username}
GROUP BY DATE_FORMAT(ul.create_time,'%Y-%m-%d') ORDER BY ul.create_time DESC LIMIT 15
